function pt = zy_extract_pt_from_contours(c) 
% PT = ZY_EXTRACT_PT_FROM_CONTOURS(C) 
% Extract contour points PT from the contour C generated by the Matlab
%   function CONTOURS. Note topological information will be discarded by this
%   fucntion. 

idx = 1;
count = 1;
pt = [];
while idx < size(c,2)
    n = c(2,idx);
    pt = [pt, c(:,idx+1:idx+n)];
    count = count+1;
    idx = idx+n+1;
end